


drop table if exists users;

CREATE TABLE IF NOT EXISTS users (
  id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL primary key,
  email VARCHAR(255) NOT NULL,
  password VARCHAR(255),
  roles VARCHAR(24)
);

